import { Component,	OnInit,	OnDestroy, ChangeDetectorRef, ViewChildren,	QueryList, ChangeDetectionStrategy,	ElementRef,	Input, AfterViewInit, ViewChild,} from "@angular/core";
import { FormGroup } from "@angular/forms";
import { XelerateService } from "src/shared/services/xelerate.service";
import { SimpleBaseClass } from "src/shared/templates/SimpleBaseClass.component";
import LoginInfo from "./login-info.json";
import { ContextProvider } from "src/shared/services/context-provider.service";
import { Maintenance } from 'src/shared/services/maintenance.service';
@Component({

	selector: "app-login",
	templateUrl: "./login.component.html",
	providers: [ContextProvider],
	changeDetection: ChangeDetectionStrategy.OnPush,
})
export class Login extends SimpleBaseClass {
    name: string = "Login";
	toggle: boolean;
	isOffline:boolean = true;
	constructor(
		public xelerate: XelerateService,
		public cp: ContextProvider,
		public changeDetectorRef: ChangeDetectorRef,
		public elmRef: ElementRef,
		public maintenance: Maintenance
	) {
		super(xelerate, LoginInfo);
	}
	ngAfterViewInit() {
		this.personalizeView();
	}
	async personalizeView() {
		this.prefs = await this.xelerate.server.getContent({ url: "assets/" + this.name + "_prefs.json" });
		this.xelerate.personalizeWidgets(this);
	}
	login(){		
        let usrId = this.xelerate.getValue("customerId");
        let password = this.xelerate.getValue("password");
        let req = { header: { userId: usrId, reqType: "Login" }, body: { userId: usrId, password: password } };
        let params = { callback: this.loginCB, context: this };
        params['reqObj'] = req;
        this.xelerate.server.postRequest(params);
	}
	loginCB(params) {
        if (params.resObj.header.status == "SUCCESS") {
            this.xelerate.data.transferData.set("userId", params.resObj.header.userId);
            this.xelerate.loadComponent({ compName: "Query_xelerate" });
        }
    }
}
